import React, { useState } from "react";

const Prices = props => {
  const [currency, setCurrency] = useState("USD");

  return (
    <div>
      <ul className="list-group">
        <li className="list-group-item">
          Bitcoin rate for {props.bpi[currency].description}:{" "}
          <span className="badge-primary">{props.bpi[currency].code}</span>
          <strong>{props.bpi[currency].rate}</strong>
        </li>
      </ul>
      <div class="btn-group m-2">
        <button onClick={(event) => setCurrency(event.target.value)} type="button" className="btn btn-primary">
          USD
        </button>
        <button onClick={(event) => setCurrency(event.target.value)} type="button" className="btn btn-primary">
          EUR
        </button>
        <button onClick={(event) => console.log(event)} type="button" className="btn btn-primary">
          GBP
        </button>
      </div>
    </div>
  );
};

export default Prices;
